import React from 'react';
import PropTypes from 'prop-types';

import './FilesDragAndDrop.scss';

export default function FilesDragAndDrop({ onUpload, children, count, formats }) {
    const drop = React.useRef(null);
    const drag = React.useRef(null);
    const [emoji, setEmoji] = React.useState(randomEmoji());
    const [dragging, setDragging] = React.useState(false);

    React.useEffect(() => {
        drop.current.addEventListener('dragover', handleDragOver);
        drop.current.addEventListener('drop', handleDrop);
        drop.current.addEventListener('dragenter', handleDragEnter);
        drop.current.addEventListener('dragleave', handleDragLeave);

        // return () => {
        //     drop.current.removeEventListener('dragover', handleDragOver);
        //     drop.current.removeEventListener('drop', handleDrop);
        //     drop.current.removeEventListener('dragenter', handleDragEnter);
        //     drop.current.removeEventListener('dragleave', handleDragLeave);
        // };
    }, []);

    const handleDragEnter = (e) => {
        e.preventDefault();
        e.stopPropagation();

        setEmoji(randomEmoji());

        if (e.target !== drag.current) {
            setDragging(true);
        }
    };

    const handleDragLeave = (e) => {
        e.preventDefault();
        e.stopPropagation();

        if (e.target === drag.current) {
            setDragging(false);
        }
    };

    const handleDragOver = (e) => {
        e.preventDefault();
        e.stopPropagation();
    };

    const handleDrop = (e) => {
        e.preventDefault();
        e.stopPropagation();

        setDragging(false);

        // this is required to convert FileList object to array
        const files = [...e.dataTransfer.files];

        // check if the provided count prop is less than uploaded count of files
        if (count && count < files.length) {
            console.log(`Only ${count} file${count !== 1 ? 's' : ''} can be uploaded at a time`);
            return;
        }

        // check if some uploaded file is not in one of the allowed formats
        if (formats && files.some((file) => !formats.some((format) => file.name.toLowerCase().endsWith(format.toLowerCase())))) {
            console.log(`Only following file formats are acceptable: ${formats.join(', ')}`);
            return;
        }

        if (files && files.length) {
            onUpload(files);
        }
    };

    const handleClickSubmit = (e) => {
        e.preventDefault();
        e.stopPropagation();

        setDragging(false);

        // this is required to convert FileList object to array
        const files = [...e.target.files];

        // check if the provided count prop is less than uploaded count of files
        if (count && count < files.length) {
            console.log(`Only ${count} file${count !== 1 ? 's' : ''} can be uploaded at a time`);
            return;
        }

        // check if some uploaded file is not in one of the allowed formats
        if (formats && files.some((file) => !formats.some((format) => file.name.toLowerCase().endsWith(format.toLowerCase())))) {
            console.log(`Only following file formats are acceptable: ${formats.join(', ')}`);
            return;
        }

        if (files && files.length) {
            onUpload(files);
        }
    };

    return (
        <label htmlFor="file">
            <div
                ref={drop}
                className='FilesDragAndDrop'
            >

                {dragging && (
                    <div ref={drag} className='FilesDragAndDropplaceholder'>
                        OM NOM NOM
                        <span
                            role='img'
                            aria-label='emoji'
                            className='areaicon'
                        >
                            {emoji}
                        </span>
                    </div>
                )}
                {children}

            </div>
            <input type="file" id='file' style={{ "opacity": "0" }} onChange={handleClickSubmit} />
        </label>
    );
}

FilesDragAndDrop.propTypes = {
    onUpload: PropTypes.func.isRequired,
};

